import React, { useState, useEffect } from 'react';
import queryString from 'query-string';
import { Link } from 'react-router-dom';
import { ReactComponent as Logo } from './heart.svg';
import { ReactComponent as ChatLogo } from './chat-logo.svg';
import { ReactComponent as Users } from './group.svg';
import { ReactComponent as SendIcon } from './send-message.svg';
import './Chat.css';
const moment = require('moment');
// Setting up Socket on a Front-End
const io = require('socket.io-client')


const Chat = ({ location }) => {
    const [enteredUser, setEnteredUser] = useState('');
    const [enteredRoom, setEnteredRoom] = useState('');
    const [justJoinedUsers, setJustJoinedUsers] = useState([]);
    const [leftUser, setLeftUser] = useState('');
    const [onlineUsers, setOnlineUsers] = useState([]);
    const [inputValue, setInputValue] = useState('');
    const [allMessages, setAllMessages] = useState([]);


    const socket = io.connect('http://localhost:5000');

    useEffect(() => {
        console.log('useEffect');
        //New user
        const newUser = queryString.parse(location.search)
        setEnteredUser(newUser.name);
        setEnteredRoom(newUser.room);
        socket.emit('newUser', (newUser), (error) => {
            console.log('error', error);
            //last callback is for network issues.
        });
        // Online users
        socket.on('onlineUsers', ({ users }) => {
            console.log('the users', users);
            setOnlineUsers([...onlineUsers, users])
        });
        // 
        return () => {
            console.log('unmounted');
            socket.disconnect();
        }
    }, [])


    useEffect(() => {
        console.log('onlineUsers', onlineUsers);
    }, [onlineUsers])


    // Welcome joined user message 
    useEffect(() => {
        console.log('useEffect2');
        // User joined the room message
        socket.on('newUserJoined', ({ user }) => {
            console.log(`${user.name} with id ${user.id} just joined the room at ${user.time}`);
            setJustJoinedUsers([...justJoinedUsers, user]);
        });
        // User left the room message
        socket.on('leftUser', ({ leftUser }) => {
            setLeftUser(leftUser);
        })



    }, [justJoinedUsers]);

    // Joined user message
    function JoinedUs(props) {
        const { name, room, time } = props.user;
        return (
            < div className="flex-body-chat-message" >
                <h5>{name}<span>{time}</span></h5>
                <p>{name} just joined the room {room}!</p>
            </div >
        )
    };
    // Left user message
    function LeftUser(props) {
        const { name, room } = props.leftUser;
        return (
            < div className="flex-body-chat-message" >
                <h5>{name}<span>{moment().format('h:mm:ss a')}</span></h5>
                <p>{name} just left the room {room}!</p>
            </div >
        )
    };
    // Welome user message
    function Welcome() {
        return (
            <div className="flex-body-chat-message">
                <p>{enteredUser} welcome to the {enteredRoom} room chat!</p>
            </div>
        )
    }
    //Online users
    function OnlineUser() {


    }




    return (
        <main className="main-block" >
            <div className="flex-main">
                {/* flex-header */}
                <div className="flex-item flex-header">
                    <h3>Chat&Me<Logo className="logo-header" /></h3>
                    <Link to={'/'} className="leave-room-button">Leave Room</Link>
                </div>
                {/* flex-body */}
                <div className="flex-item flex-body">
                    {/* flex-body-nav */}
                    <div className="flex-body-nav">
                        <h3><ChatLogo className="logo-nav" />Room:</h3>
                        <p>Room1</p>
                        <h3><Users className="logo-nav" />Users:</h3>
                        {onlineUsers.length > 0 ? onlineUsers.map((user, index) => {
                            return (
                                <p key={index}>{user.name}</p>
                            )
                        }) : null}

                        {/* <p>Body</p>
                        <p>Paul</p>
                        <p>Alan</p>
                        <p>Tony</p> */}
                    </div>

                    {/* flex-body-chat */}
                    <div className="flex-body-chat">

                        {/* Welcome message */}
                        <Welcome />
                        {/* User joined message */}
                        {justJoinedUsers.length > 0 ? justJoinedUsers.map((user, index) => {
                            return <JoinedUs key={index} user={user} />
                        }) : null}
                        {/* User left message */}
                        {leftUser ? <LeftUser leftUser={leftUser} /> : null}

                    </div>
                </div>
                {/* flex-footer */}
                <div className="flex-item flex-footer">
                    <div className="footer-wrapper">
                        <input
                            onChange={(e) => setInputValue(e.target.value)}
                            className="send-message-input"
                            placeholder="Enter message" />
                        <button
                            onClick={() => setAllMessages([...allMessages, inputValue])}
                            className="send-message-button"><SendIcon className="send-message-icon" /></button>

                    </div>
                </div>
            </div>
        </main >
    )
}

export default Chat
